import React, { useState } from 'react';
import axios from 'axios';

function QuanLyXe() {
  const [formData, setFormData] = useState({ 
    SO_CCCD_CHU_XE: '', 
    BIEN_SO: '', 
    LOAI_XE: '', 
    HANG_XE: '', 
    NGAY_DANG_KY: '' 
  });
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResult, setSearchResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.post('http://localhost:5000/api/xe', formData);
      alert('‚úÖ ' + response.data.message);
      setFormData({ 
        SO_CCCD_CHU_XE: '', 
        BIEN_SO: '', 
        LOAI_XE: '', 
        HANG_XE: '', 
        NGAY_DANG_KY: '' 
      });
    } catch (error) {
      alert('‚ùå L·ªói: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    setSearchResult(null);
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:5000/api/xe/tracuu/${searchTerm}`);
      if (response.data && response.data.length > 0) {
        setSearchResult(response.data[0]);
      } else {
        alert('‚ö†Ô∏è Kh√¥ng t√¨m th·∫•y xe v·ªõi bi·ªÉn s·ªë n√†y.');
      }
    } catch (error) {
      if (error.response?.status === 404) {
        alert('‚ö†Ô∏è Kh√¥ng t√¨m th·∫•y xe v·ªõi bi·ªÉn s·ªë n√†y.');
      } else {
        alert('‚ùå L·ªói: ' + (error.response?.data?.message || error.message));
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2 style={{
        fontSize: '2em',
        marginBottom: '30px',
        color: '#333',
        textAlign: 'center',
        textShadow: '2px 2px 4px rgba(0,0,0,0.1)'
      }}>
        üöó Qu·∫£n l√Ω Ph∆∞∆°ng ti·ªán Giao th√¥ng
      </h2>

      <div style={{ 
        display: 'grid', 
        gridTemplateColumns: 'repeat(auto-fit, minmax(400px, 1fr))', 
        gap: '30px' 
      }}>
        {/* Form ƒêƒÉng k√Ω */}
        <div style={{
          background: 'white',
          borderRadius: '15px',
          padding: '30px',
          boxShadow: '0 10px 30px rgba(0,0,0,0.1)',
          border: '1px solid #f0f0f0'
        }}>
          <h3 style={{ 
            color: '#667eea', 
            marginBottom: '25px',
            fontSize: '1.5em',
            display: 'flex',
            alignItems: 'center',
