import React, { useState, useEffect } from 'react';
import axios from 'axios';

function DangKyTru() {
  const [activeTab, setActiveTab] = useState('tamtru');
  const [list, setList] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const endpoint = activeTab === 'tamtru' ? 'tamtru' : 'tamvang';
      try {
        const response = await axios.get(`http://localhost:5000/api/tru/${endpoint}`);
        setList(response.data);
      } catch (error) {
        console.error(`Lỗi khi tải danh sách ${activeTab}:`, error);
      }
    };
    fetchData();
  }, [activeTab]);

  return (
    <div>
      <h2 className="content-header">Quản lý Cư trú</h2>
      <div className="tab-container">
        <button className={`tab-button ${activeTab === 'tamtru' ? 'active' : ''}`} onClick={() => setActiveTab('tamtru')}>
          Danh sách Tạm trú
        </button>
        <button className={`tab-button ${activeTab === 'tamvang' ? 'active' : ''}`} onClick={() => setActiveTab('tamvang')}>
          Danh sách Tạm vắng
        </button>
      </div>

      <table className="data-table">
        <thead>
          <tr>
            <th>Số CCCD</th>
            <th>Loại</th>
            <th>Nơi cư trú</th>
            <th>Từ ngày</th>
            <th>Đến ngày</th>
            <th>Tỉnh/Thành</th>
          </tr>
        </thead>
        <tbody>
          {list.map((item, index) => (
            <tr key={index}>
              <td>{item.so_cccd}</td>
              <td>{item.loai_tru}</td>
              <td>{item.noi_tru}</td>
              <td>{new Date(item.tu_ngay).toLocaleDateString()}</td>
              <td>{item.den_ngay ? new Date(item.den_ngay).toLocaleDateString() : 'N/A'}</td>
              <td>{item.tinh}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default DangKyTru;
