import React, { useState, useEffect, useCallback } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';

function ChiTietCongDan() {
  const { cccd } = useParams();
  const [congDan, setCongDan] = useState(null);
  const [giaDinh, setGiaDinh] = useState({ voChong: null, con: [] });
  const [loading, setLoading] = useState(true);

  const fetchData = useCallback(async () => {
    setLoading(true);
    try {
      // 1. Lấy thông tin công dân
      const congDanRes = await axios.get(`http://localhost:5000/api/congdan/${cccd}`);
      const currentCongDan = congDanRes.data;
      setCongDan(currentCongDan);

      let familyInfo = { voChong: null, con: [] };

      // 2. Nếu đã kết hôn, tìm thông tin vợ/chồng
      if (currentCongDan.tinh_trang_hon_nhan === 'KET_HON') {
        const ketHonRes = await axios.get('http://localhost:5000/api/kethon');
        const honNhan = ketHonRes.data.find(h => 
            (h.so_cccd_chong === cccd || h.so_cccd_vo === cccd) && h.trang_thai === 'KET_HON'
        );
        if (honNhan) {
          const cccdVochong = honNhan.so_cccd_chong === cccd ? honNhan.so_cccd_vo : honNhan.so_cccd_chong;
          const voChongRes = await axios.get(`http://localhost:5000/api/congdan/${cccdVochong}`);
          familyInfo.voChong = voChongRes.data;
        }
      }

      // 3. Tìm thông tin các con (giả sử giấy khai sinh có trường SO_CCCD của con)
      const khaiSinhRes = await axios.get('http://localhost:5000/api/khaisinh'); // Đã sửa API endpoint
      const dsCon = khaiSinhRes.data.filter(ks => ks.so_cccd_cha === cccd || ks.so_cccd_me === cccd);

      if (dsCon.length > 0) {
          // Lấy thông tin chi tiết của từng đứa con
          const conPromises = dsCon.map(con => axios.get(`http://localhost:5000/api/congdan/${con.so_cccd}`));
          const conResponses = await Promise.all(conPromises);
          familyInfo.con = conResponses.map(res => res.data);
      }

      setGiaDinh(familyInfo);
    } catch (error) {
      console.error("Lỗi khi tải chi tiết công dân:", error);
      setCongDan(null); // Setเป็น null nếu lỗi
    } finally {
      setLoading(false);
    }
  }, [cccd]);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  if (loading) {
    return <div style={{ padding: '20px', textAlign: 'center', fontSize: '1.5em' }}>Đang tải...</div>;
  }

  if (!congDan) {
    return <div style={{ padding: '20px', textAlign: 'center', fontSize: '1.5em' }}>Không tìm thấy thông tin công dân.</div>;
  }

  return (
    <div style={{ padding: '20px' }}>
      <h2 style={{
        fontSize: '2em',
        marginBottom: '30px',
        color: '#333',
        textAlign: 'center',
        textShadow: '2px 2px 4px rgba(0,0,0,0.1)'
      }}>
        Chi Tiết Công Dân
      </h2>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(350px, 1fr))', gap: '30px' }}>
        {/* Thông tin cá nhân */}
        <InfoCard title="Thông tin cá nhân">
          <InfoRow label="Họ và tên" value={congDan.ho_ten} />
          <InfoRow label="Số CCCD" value={congDan.so_cccd} />
          <InfoRow label="Năm sinh" value={congDan.nam_sinh} />
          <InfoRow label="Giới tính" value={congDan.gioi_tinh} />
          <InfoRow label="Quê quán" value={congDan.que_quan} />
          <InfoRow label="Địa chỉ" value={congDan.dia_chi_thuong_tru} />
          <InfoRow label="Hôn nhân" value={congDan.tinh_trang_hon_nhan} />
        </InfoCard>

        {/* Thông tin gia đình */}
        <InfoCard title="Thông tin gia đình">
          <InfoRow 
            label="Vợ/Chồng" 
            value={giaDinh.voChong ? `${giaDinh.voChong.ho_ten} (${giaDinh.voChong.so_cccd})` : 'Chưa có'} 
          />
          <div>
            <p style={{ fontWeight: '600', color: '#555', marginBottom: '10px' }}>Các con:</p>
            {giaDinh.con.length > 0 ? (
              <ul style={{ listStyle: 'none', padding: 0, margin: 0, display: 'flex', flexDirection: 'column', gap: '10px' }}>
                {giaDinh.con.map(c => (
                  <li key={c.so_cccd} style={{ background: '#f9f9f9', padding: '10px', borderRadius: '8px' }}>
                    <Link to={`/cong-dan/${c.so_cccd}`} style={{ textDecoration: 'none', color: '#333' }}>
                      <strong>{c.ho_ten}</strong> ({c.so_cccd})
                    </Link>
                  </li>
                ))}
              </ul>
            ) : (
              <p>Chưa có thông tin.</p>
            )}
          </div>
        </InfoCard>
      </div>
    </div>
  );
}

// Reusable Components for this page
const InfoCard = ({ title, children }) => (
  <div style={{
    background: 'white',
    borderRadius: '15px',
    padding: '30px',
    boxShadow: '0 10px 30px rgba(0,0,0,0.1)',
    border: '1px solid #f0f0f0',
    display: 'flex',
    flexDirection: 'column',
    gap: '20px'
  }}>
    <h3 style={{ color: '#667eea', marginTop: 0, marginBottom: '10px', fontSize: '1.5em', borderBottom: '2px solid #f0f0f0', paddingBottom: '10px' }}>
      {title}
    </h3>
    {children}
  </div>
);

const InfoRow = ({ label, value }) => (
  <div>
    <p style={{ margin: 0, color: '#555', fontWeight: '600' }}>{label}</p>
    <p style={{ margin: '5px 0 0', color: '#333', fontSize: '1.1em' }}>{value || 'Chưa có'}</p>
  </div>
);

export default ChiTietCongDan;
