import React, { useState } from 'react';
import axios from 'axios';

function LyHon() {
  const [formData, setFormData] = useState({
    ID_KET_HON: '',
    NGAY_LY_HON: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/lyhon', formData);
      alert(response.data.message);
    } catch (error) {
      alert('Đã xảy ra lỗi: ' + error.response.data.message);
    }
  };

  return (
    <div>
      <h2>Đăng Ký Ly Hôn</h2>
      <form onSubmit={handleSubmit}>
        <input name="ID_KET_HON" placeholder="ID Kết Hôn" onChange={handleChange} required /><br />
        <input name="NGAY_LY_HON" type="date" placeholder="Ngày Ly Hôn" onChange={handleChange} required /><br />
        <button type="submit">Xác Nhận Ly Hôn</button>
      </form>
    </div>
  );
}

export default LyHon;
