import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ThongKe() {
  const [gioiTinh, setGioiTinh] = useState([]);
  const [honNhan, setHonNhan] = useState([]);
  const [doTuoi, setDoTuoi] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const [gioiTinhRes, honNhanRes, doTuoiRes] = await Promise.all([
          axios.get('http://localhost:5000/api/thongke/gioitinh'),
          axios.get('http://localhost:5000/api/thongke/honnhan'),
          axios.get('http://localhost:5000/api/thongke/dotuoi'),
        ]);
        setGioiTinh(gioiTinhRes.data);
        setHonNhan(honNhanRes.data);
        setDoTuoi(doTuoiRes.data);
      } catch (error) {
        console.error("Lỗi khi tải dữ liệu thống kê:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const StatCard = ({ title, data, isObject = false }) => (
    <div className="form-container" style={{ 
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      color: 'white',
      borderRadius: '15px',
      padding: '25px',
      boxShadow: '0 10px 30px rgba(0,0,0,0.2)'
    }}>
      <h3 style={{ 
        marginBottom: '20px', 
        fontSize: '1.4em',
        fontWeight: 'bold',
        borderBottom: '2px solid rgba(255,255,255,0.3)',
        paddingBottom: '10px'
      }}>{title}</h3>

      {loading ? (
        <div style={{ textAlign: 'center', padding: '20px' }}>
          <div style={{ 
            border: '3px solid rgba(255,255,255,0.3)',
            borderTop: '3px solid white',
            borderRadius: '50%',
            width: '40px',
            height: '40px',
            animation: 'spin 1s linear infinite',
            margin: '0 auto'
          }} />
        </div>
      ) : (
        <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
          {isObject ? (
            Object.entries(data).map(([key, value]) => (
              <div key={key} style={{ 
                background: 'rgba(255,255,255,0.15)',
                padding: '15px',
                borderRadius: '10px',
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
                transition: 'transform 0.2s',
                cursor: 'pointer'
              }}
              onMouseEnter={(e) => e.currentTarget.style.transform = 'translateX(5px)'}
              onMouseLeave={(e) => e.currentTarget.style.transform = 'translateX(0)'}
              >
                <span style={{ fontSize: '1.1em' }}>
                  {key === 'Duoi 18' ? 'Dưới 18 tuổi' : 
                   key === 'Tren 50' ? 'Trên 50 tuổi' : 
                   `${key} tuổi`}
                </span>
                <span style={{ 
                  fontSize: '1.5em', 
                  fontWeight: 'bold',
                  background: 'rgba(255,255,255,0.2)',
                  padding: '5px 15px',
                  borderRadius: '20px'
                }}>
                  {value}
                </span>
              </div>
            ))
          ) : (
            data.map((item, index) => (
              <div key={index} style={{ 
                background: 'rgba(255,255,255,0.15)',
                padding: '15px',
                borderRadius: '10px',
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
                transition: 'transform 0.2s',
                cursor: 'pointer'
              }}
              onMouseEnter={(e) => e.currentTarget.style.transform = 'translateX(5px)'}
              onMouseLeave={(e) => e.currentTarget.style.transform = 'translateX(0)'}
              >
                <span style={{ fontSize: '1.1em' }}>
                  {item.gioi_tinh || item.tinh_trang_hon_nhan}
                </span>
                <span style={{ 
                  fontSize: '1.5em', 
                  fontWeight: 'bold',
                  background: 'rgba(255,255,255,0.2)',
                  padding: '5px 15px',
                  borderRadius: '20px'
                }}>
                  {item.so_luong.toString()}
                </span>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );

  return (
    <div style={{ padding: '20px' }}>
      <h2 className="content-header" style={{
        fontSize: '2em',
        marginBottom: '30px',
        color: '#333',
        textAlign: 'center',
        textShadow: '2px 2px 4px rgba(0,0,0,0.1)'
      }}>
        📊 Thống kê Dân số
      </h2>

      <div style={{ 
        display: 'grid', 
        gridTemplateColumns: 'repeat(auto-fit, minmax(350px, 1fr))', 
        gap: '25px',
        marginBottom: '25px'
      }}>
        <StatCard title="👥 Theo Giới tính" data={gioiTinh} />
        <StatCard title="💍 Tình trạng Hôn nhân" data={honNhan} />
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: '1fr' }}>
        <StatCard title="🎂 Theo Độ tuổi" data={doTuoi} isObject={true} />
      </div>

      <style>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
}

export default ThongKe;
