import React, { useState, useEffect } from 'react';
import axios from 'axios';

function HoKhau() {
  const [hoKhauList, setHoKhauList] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/hokhau');
        setHoKhauList(response.data);
      } catch (error) {
        console.error("Lỗi khi tải danh sách hộ khẩu:", error);
      }
    };
    fetchData();
  }, []);

  return (
    <div>
      <h2 className="content-header">Danh sách Hộ khẩu</h2>
      <table className="data-table">
        <thead>
          <tr>
            <th>Số Hộ Khẩu</th>
            <th>CCCD Chủ Hộ</th>
            <th>Địa chỉ</th>
            <th>Tỉnh/Thành</th>
            <th>Thành viên</th>
          </tr>
        </thead>
        <tbody>
          {hoKhauList.map((hk) => (
            <tr key={hk.so_ho_khau}>
              <td>{hk.so_ho_khau}</td>
              <td>{hk.chu_ho}</td>
              <td>{hk.dia_chi}</td>
              <td>{hk.tinh}</td>
              <td>{hk.danh_sach_thanh_vien.join(', ')}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default HoKhau;
