import React, { useState } from 'react';
import axios from 'axios';

function KetHon() {
  const [formData, setFormData] = useState({
    SO_CCCD_CHONG: '',
    SO_CCCD_VO: '',
    NGAY_DANG_KY: '',
    NOI_DANG_KY: '',
    TINH: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/kethon', formData);
      alert(response.data.message);
    } catch (error) {
      alert('Đã xảy ra lỗi: ' + error.response.data.message);
    }
  };

  return (
    <div>
      <h2>Đăng Ký Kết Hôn</h2>
      <form onSubmit={handleSubmit}>
        <input name="SO_CCCD_CHONG" placeholder="Số CCCD Chồng" onChange={handleChange} required /><br />
        <input name="SO_CCCD_VO" placeholder="Số CCCD Vợ" onChange={handleChange} required /><br />
        <input name="NGAY_DANG_KY" type="date" placeholder="Ngày Đăng Ký" onChange={handleChange} required /><br />
        <input name="NOI_DANG_KY" placeholder="Nơi Đăng Ký" onChange={handleChange} required /><br />
        <input name="TINH" placeholder="Tỉnh/Thành phố" onChange={handleChange} required /><br />
        <button type="submit">Đăng Ký</button>
      </form>
    </div>
  );
}

export default KetHon;
