import React, { useState } from 'react';
import axios from 'axios';

function KhaiSinh() {
  const [formData, setFormData] = useState({
    SO_GIAY: '',
    SO_CCCD: '', // CCCD của con
    NGAY_SINH: '',
    NOI_SINH: '',
    SO_CCCD_CHA: '',
    SO_CCCD_ME: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Lưu ý: Backend cần có chức năng tạo công dân mới trước khi khai sinh
      // Ở đây ta giả định backend sẽ tự xử lý
      const response = await axios.post('http://localhost:5000/api/khaisinh', formData);
      alert(response.data.message);
    } catch (error) {
      alert('Đã xảy ra lỗi: ' + error.response.data.message);
    }
  };

  return (
    <div>
      <h2>Đăng Ký Khai Sinh</h2>
      <form onSubmit={handleSubmit}>
        <input name="SO_GIAY" placeholder="Số Giấy Khai Sinh" onChange={handleChange} required /><br />
        <input name="SO_CCCD" placeholder="Số CCCD của con" onChange={handleChange} required /><br />
        <input name="NGAY_SINH" type="date" placeholder="Ngày Sinh" onChange={handleChange} required /><br />
        <input name="NOI_SINH" placeholder="Nơi Sinh" onChange={handleChange} required /><br />
        <input name="SO_CCCD_CHA" placeholder="Số CCCD Cha" onChange={handleChange} required /><br />
        <input name="SO_CCCD_ME" placeholder="Số CCCD Mẹ" onChange={handleChange} required /><br />
        <button type="submit">Đăng Ký Khai Sinh</button>
      </form>
    </div>
  );
}

export default KhaiSinh;
